[				'label' => __( 'Tabs', 'eventes' ),				'tab' => Controls_Manager::TAB_STYLE,			]		);		$this->add_control(			'navigation_width',			[				'label' => __( 'Navigation Width', 'eventes' ),				'type' => Controls_Manager::SLIDER,				'default' => [					'unit' => '%',				],				'range' => [					'%' => [						'min' => 10,						'max' => 50,					],				],				'selectors' => [					'{{WRAPPER}} .elementor-widget-container' => 'width: {{SIZE}}{{UNIT}}',				],				'condition' => [					'type' => 'vertical',				],			]		);		$this->add_control(			'border_width',			[				'label' => __( 'Border Width', 'eventes' ),				'type' => Controls_Manager::SLIDER,				'default' => [					'size' => 1,				],				'range' => [					'px' => [						'min' => 0,						'max' => 10,					],				],				'selectors' => [					'{{WRAPPER}} .elementor-tab-title, {{WRAPPER}} .elementor-tab-title:before, {{WRAPPER}} .elementor-tab-title:after, {{WRAPPER}} .elementor-tab-content, {{WRAPPER}} .elementor-tabs-content-wrapper' => 'border-width: {{SIZE}}{{UNIT}};',				],			]		);		$this->add_control(			'border_color',			[				'label' => __( 'Border Color', 'eventes' ),				'type' => Controls_Manager::COLOR,				'scheme' => [					'type' => Scheme_Color::get_type(),					'value' => Scheme_Color::COLOR_1,				],				'default' => '#fff',				'selectors' => [										'{{WRAPPER}} .spicel-features ul li:hover::before' => 'border-color: {{VALUE}} !important;',				],			]		);		$this->add_control(			'primary',			[				'label' => __( 'Primary Color', 'eventes' ),				'type' => Controls_Manager::COLOR,								'scheme' => [					'type' => Scheme_Color::get_type(),					'value' => Scheme_Color::COLOR_1,				],				'default' => '#e72c30',				'selectors' => [					'{{WRAPPER}} .spicel-features a.nav-item.nav-link.active,{{WRAPPER}} .spicel-features a.nav-item.nav-link:hover,{{WRAPPER}} .spicel-features .rbtn,{{WRAPPER}} .spicel-features ul li:hover::before' => 'background: {{VALUE}} !important;',					'{{WRAPPER}} .spicel-features .rbtn' => 'border-color: {{VALUE}}',					'{{WRAPPER}} .spicel-features .title h5,{{WRAPPER}} .spicel-features ul li::before' => 'color: {{VALUE}};',					'{{WRAPPER}} #preview nav > div a.nav-item.nav-link.active::after' => 'border-top-color: {{VALUE}} !important;',				],							]		);				$this->add_control(			'primary_hover',			[				'label' => __( 'Primary Hover', 'eventes' ),				'type' => Controls_Manager::COLOR,								'scheme' => [					'type' => Scheme_Color::get_type(),					'value' => Scheme_Color::COLOR_1,				],				'default' => '#fff',				'selectors' => [										'{{WRAPPER}} .spicel-features ul li:hover::before' => 'color: {{VALUE}};',										'{{WRAPPER}} .spicel-features .rbtn:hover' => 'background: {{VALUE}} !important;;',									],							]		);		$this->add_control(			'heading_content',			[				'label' => __( 'Content', 'eventes' ),				'type' => Controls_Manager::HEADING,				'separator' => 'before',			]		);		$this->add_control(			'content_color',			[				'label' => __( 'Color', 'eventes' ),				'type' => Controls_Manager::COLOR,				'scheme' => [					'type' => Scheme_Color::get_type(),					'value' => Scheme_Color::COLOR_1,				],				'default' => '#7b76ab',				'selectors' => [										'{{WRAPPER}} .spicel-features ul li,{{WRAPPER}} .spicel-features p' => 'color: {{VALUE}};',						],			]		);		$this->add_group_control(			Group_Control_Typography::get_type(),			[				'name' => 'content_typography',				'selector' => '{{WRAPPER}} .spicel-features ul li,{{WRAPPER}} .spicel-features p',				'scheme' => Scheme_Typography::TYPOGRAPHY_3,			]		);		$this->end_controls_section();		}		protected function render( $instance = [] ) {		eventes_el()->get_section('event-tab', [			'rows' => $this->get_settings('the_row'),			'paroller' => $this->get_settings('p